shift_size = 160;frame_size = 400;frame_size_2n = 2**(ceil(log2(frame_size)));[y,Fs,bits] = wavread("MFCC/gas_station.wav");len = length(y)disp("1. Length is"), disp(1000 * (length(y) / Fs));dc_offset = mean(y)fprintf("2. DC Offset is %f\n", dc_offset);% Remove DC Offsety = y - dc_offset;total_frames = ceil(1 + ((len - frame_size) / shift_size));% paddingy = [y.' zeros(1,(total_frames*frame_size) - len)].';spe_global = y;for i = 2:length(y)  spe_global(i) = y(i) - 0.97*y(i-1);endforstart_frame = 160*(50 - 1) + 1;stop_frame = start_frame + 399;sof_50 = y(160*(50 - 1) + 1:160*(50 - 1) + 400);logE_50 = max(-50, log(sum(sof_50.^2)));logE_50%L = length(sof_50)%Y = fft(sof_50);%P2 = abs(Y);%P1 = P2(1:L/2+1);%P1(2:end-1) = 2*P1(2:end-1);%f = Fs*(0:(L/2))/L;%plot(f,P1) spe_50 = spe_global(start_frame:stop_frame);%plot(spe_50);%plot(abs(fft(sof_50)));%L = length(spe_50)%Y = fft(spe_50);%P2 = abs(Y/L);%P1 = P2(1:L/2+1);%P1(2:end-1) = 2*P1(2:end-1);%f = Fs*(0:(L/2))/L;%plot(f,P1)spe_50p = [(spe_50 .* hamming(length(spe_50))).' zeros(1, 512 - length(spe_50))].';spect_50 = abs(fft(spe_50p));disp('spec'), disp(size(spect_50));L = length(spect_50)Y = spect_50;P2 = abs(Y/L);P1 = P2(1:L/2+1);P1(2:end-1) = 2*P1(2:end-1);f = Fs*(0:(L/2))/L;%f(1)%f(257)%plot(f, P1)%xlabel('Frequency (Hz)');load('MFCC/mel_filters.mat');%plot((0:256)'/256*8000, mel_filters);%title('Mel filter banks');%xlabel('frequency (Hz)');%ylabel('weight');[v, args] = max(mel_filters);spect_50_mel = mel_filters.' * spect_50(1:257);spect_50_melmfsc = 1:23;for i = 1:length(mfsc)  mfsc(i) = log(spect_50_mel(i));  if (mfsc(i) < -50)    mfsc(i) = -50;  endifendforcs = 1:23;for i = 1:23  s = 0;  for j = 1:23    s += mfsc(j) * cos((j - 0.5) * (pi*i/23));  end  cs(i) = s;endfor