[y,Fs,bits] = wavread("MFCC/gas_station.wav");disp("1. Length is"), disp(1000 * (length(y) / Fs));dc_offset = mean(y)fprintf("2. DC Offset is %f\n", dc_offset);% Remove DC Offsety = y - dc_offset;sof_50 = y(160*(50 - 1) + 1:160*(50 - 1) + 400);length(sof_50)logE_50 = max(-50, log(sum(sof_50.^2)));logE_50%L = length(sof_50)%Y = fft(sof_50);%P2 = abs(Y/L);%P1 = P2(1:L/2+1);%P1(2:end-1) = 2*P1(2:end-1);%f = Fs*(0:(L/2))/L;%plot(f,P1) spe_50 = sof_50;for i = 2:400  spe_50(i) = sof_50(i) - 0.97*sof_50(i-1);endL = length(spe_50)Y = fft(spe_50);P2 = abs(Y/L);P1 = P2(1:L/2+1);P1(2:end-1) = 2*P1(2:end-1);f = Fs*(0:(L/2))/L;plot(f,P1) 